<script setup lang="ts">
import { onMounted, ref } from 'vue'
import { initFlowbite } from 'flowbite'
import ImageMsg from './components/ImageMsg.vue'
import TxtMsg from './components/TxtMsg.vue'

const loading = ref(true)

onMounted(() => {
  initFlowbite()
  loading.value = false
})

const step = ref(0)
const msg = ref()
function scrollToBottom() {
  const currentScroll = msg.value.scrollTop // 已经被卷掉的高度
  const clientHeight = msg.value.clientHeight // 浏览器高度
  const scrollHeight = msg.value.scrollHeight // 总高度
  if (scrollHeight - 10 > currentScroll + clientHeight) {
    window.requestAnimationFrame(smoothscroll)
    window.scrollTo(0, currentScroll + (scrollHeight - currentScroll - clientHeight) / 2)
  }
}

const content = ref(' ')

function next() {
  loading.value = true
  step.value++

  content.value = ' '
  setTimeout(() => {
    loading.value = false

    scrollToBottom()
  }, 3000)
}
</script>

<template>
  <div id="default-modal" tabindex="-1" aria-hidden="true" class="hidden overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 justify-center items-center w-full md:inset-0 h-[calc(100%-1rem)] max-h-full">
